PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime, "role" varchar not null default 'admin', "invitation_token" varchar, "invitation_sent_at" datetime, "invitation_accepted_at" datetime, "active" tinyint(1) not null default '1', "api_token" varchar);
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE TABLE IF NOT EXISTS "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE TABLE IF NOT EXISTS "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "categories" ("id" integer primary key autoincrement not null, "name" varchar not null, "order" integer not null default '0', "created_at" datetime, "updated_at" datetime, "relevance" integer not null default '0', "show_on_cover" tinyint(1) not null default '0', "cover_title" varchar, "cover_subtitle" varchar, "view_scope" varchar not null default 'menu');
CREATE TABLE IF NOT EXISTS "dishes" ("id" integer primary key autoincrement not null, "name" varchar not null, "description" text not null, "price" numeric not null, "image" varchar, "category_id" integer not null, "created_at" datetime, "updated_at" datetime, "visible" tinyint(1) not null default '1', "position" integer not null default '0', "featured_on_cover" tinyint(1) not null default '0', foreign key("category_id") references "categories"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "cantina_items" ("id" integer primary key autoincrement not null, "name" varchar not null, "description" text, "price" numeric not null, "created_at" datetime, "updated_at" datetime, "image" varchar, "category_id" integer not null);
CREATE TABLE IF NOT EXISTS "cantina_categories" ("id" integer primary key autoincrement not null, "name" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "cocktails" ("id" integer primary key autoincrement not null, "name" varchar not null, "description" text, "price" numeric not null, "category_id" integer not null, "created_at" datetime, "updated_at" datetime, "image" varchar, "visible" tinyint(1) not null default '1', "position" integer not null default '0', "featured_on_cover" tinyint(1) not null default '0', foreign key("category_id") references "cocktail_categories"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "settings" ("id" integer primary key autoincrement not null, "background_image_cover" varchar, "background_image_menu" varchar, "created_at" datetime, "updated_at" datetime, "background_image_cocktails" varchar, "background_image_wines" varchar, "font_family" varchar, "text_color" varchar, "opacity" numeric, "card_opacity" numeric, "button_color" varchar, "text_color_cover" varchar, "text_color_menu" varchar, "text_color_cocktails" varchar, "text_color_wines" varchar, "card_opacity_cover" numeric, "card_opacity_menu" numeric, "card_opacity_cocktails" numeric, "card_opacity_wines" numeric, "font_family_cover" varchar, "font_family_menu" varchar, "font_family_cocktails" varchar, "font_family_wines" varchar, "button_color_cover" varchar, "button_color_menu" varchar, "button_color_cocktails" varchar, "button_color_wines" varchar, "logo" varchar, "facebook_url" varchar, "twitter_url" varchar, "instagram_url" varchar, "phone_number" varchar, "business_hours" text, "category_name_bg_color_menu" varchar, "category_name_text_color_menu" varchar, "category_name_font_size_menu" integer, "category_name_bg_color_cocktails" varchar, "category_name_text_color_cocktails" varchar, "category_name_font_size_cocktails" integer, "category_name_bg_color_wines" varchar, "category_name_text_color_wines" varchar, "category_name_font_size_wines" integer, "card_bg_color_menu" varchar, "card_bg_color_cocktails" varchar, "card_bg_color_wines" varchar, "button_font_size_cover" integer default '18', "fixed_bottom_font_size" integer default '14', "fixed_bottom_font_color" varchar default '#ffffff', "background_image" varchar, "category_name_bg_color" varchar, "category_name_text_color" varchar, "category_name_font_size" integer, "button_label_menu" varchar default 'Menú', "button_label_cocktails" varchar default 'Cócteles', "button_label_wines" varchar default 'Cafe', "button_label_events" varchar default 'Eventos especiales', "button_label_vip" varchar default 'Lista VI