r, "featured_tab_text_color" varchar, "cover_cta_vip_bg_color" varchar, "cover_cta_vip_text_color" varchar, "show_cta_menu" tinyint(1) not null default '1', "show_cta_cafe" tinyint(1) not null default '1', "show_cta_cocktails" tinyint(1) not null default '1', "show_cta_events" tinyint(1) not null default '1', "show_cta_reservations" tinyint(1) not null default '1', "show_cta_vip" tinyint(1) not null default '1');
CREATE TABLE IF NOT EXISTS "popups" ("id" integer primary key autoincrement not null, "title" varchar not null, "image" varchar not null, "view" varchar not null, "start_date" datetime not null, "end_date" datetime not null, "active" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, "repeat_days" varchar);
CREATE TABLE IF NOT EXISTS "wine_types" ("id" integer primary key autoincrement not null, "name" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "grapes" ("id" integer primary key autoincrement not null, "name" varchar not null, "wine_type_id" integer, "description" text, "created_at" datetime, "updated_at" datetime, foreign key("wine_type_id") references "wine_types"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "regions" ("id" integer primary key autoincrement not null, "name" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "food_pairings" ("id" integer primary key autoincrement not null, "name" varchar not null, "created_at" datetime, "updated_at" datetime, "dish_id" integer);
CREATE TABLE IF NOT EXISTS "grape_wine" ("id" integer primary key autoincrement not null, "wine_id" integer not null, "grape_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("wine_id") references "wines"("id") on delete cascade, foreign key("grape_id") references "grapes"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "food_pairing_wine" ("id" integer primary key autoincrement not null, "wine_id" integer not null, "food_pairing_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("wine_id") references "wines"("id") on delete cascade, foreign key("food_pairing_id") references "food_pairings"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "dish_food_pairing" ("id" integer primary key autoincrement not null, "dish_id" integer not null, "food_pairing_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("dish_id") references "dishes"("id") on delete cascade, foreign key("food_pairing_id") references "food_pairings"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "dish_wine" ("id" integer primary key autoincrement not null, "dish_id" integer not null, "wine_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("dish_id") references "dishes"("id") on delete cascade, foreign key("wine_id") references "wines"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "wines" ("id" integer primary key autoincrement not null, "name" varchar not null, "description" text, "price" numeric not null, "category_id" integer, "image" varchar, "created_at" datetime, "updated_at" datetime, "visible" tinyint(1) not null default '1', "region_id" integer, "type_id" integer, "position" integer not null default '0', "featured_on_cover" tinyint(1) not null default '0', foreign key("category_id") references "wine_categories"("id") on delete cascade on update no action);
CREATE TABLE IF NOT EXISTS "events" ("id" integer primary key autoincrement not null, "title" varchar not null, "slug" varchar not null, "description" text, "start_at" datetime not null, "end_at" datetime, "hero_image" varchar, "map_image" varchar, "is_active" tinyint(1) not null default '0', "additional_info" text, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "event_sections" ("id" integer primary key autoincrement not null, "event_id" integer not null, "name" varchar not null, "slug" varchar not null, "description" text, "capacity" integer not null default '0', "available_slots" integer not null default '0', "price_per_person" numeric not null default '0', "flat_price" numeric, "layout_coordinates" text, "is_active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime, foreign key("event_id") references "events"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "event_tickets" ("id" integer primary key autoincrement not null, "event_id" integer not null, "event_section_id" integer not null, "customer_name" varchar not null, "customer_email" varchar, "guest_count" integer not null default '1', "total_paid" numeric not null, "ticket_code" varchar not null, "meta" text, "status" varchar check ("status" in ('pending', 'paid', 'cancelled')) not null default 'pending', "created_at" datetime, "updated_at" datetime, foreign key("event_id") references "events"("id") on delete cascade, foreign key("event_section_id") references "event_sections"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "event_notifications" ("id" integer primary key autoincrement not null, "event_id" integer, "name" varchar not null, "email" varchar not null, "confirmed" tinyint(1) not null default '0', "confirmation_token" varchar, "created_at" datetime, "updated_at" datetime, foreign key("event_id") references "events"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "event_promotions" ("id" integer primary key autoincrement not null, "title" varchar not null, "subject" varchar not null, "preview_text" varchar, "hero_image" varchar, "body_html" text not null, "attachments" text, "status" varchar check ("status" in ('draft', 'sending', 'sent', 'failed')) not null default 'draft', "sent_at" datetime, "send_count" integer not null default '0', "send_error" text, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "loyalty_customers" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "phone" varchar, "points" integer not null default '0', "last_visit_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "loyalty_rewards" ("id" integer primary key autoincrement not null, "title" varchar not null, "description" text, "points_required" integer not null, "active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "loyalty_visits" ("id" integer primary key autoincrement not null, "server_id" integer not null, "expected_name" varchar not null, "expected_email" varchar not null, "expected_phone" varchar not null, "qr_token" varchar not null, "status" varchar check ("status" in ('pending', 'confirmed', 'expired')) not null default 'pending', "points_awarded" integer not null default '0', "confirmed_at" datetime, "customer_snapshot" text, "created_at" datetime, "updated_at" datetime, foreign key("server_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "loyalty_redemptions" ("id" integer primary key autoincrement not null, "loyalty_customer_id" integer not null, "loyalty_reward_id" integer not null, "points_used" integer not null, "status" varchar check ("status" in ('pending', 'approved', 'rejected')) not null default 'pending', "approved_by" integer, "notes" text, "created_at" datetime, "updated_at" datetime, foreign key("loyalty_customer_id") references "loyalty_customers"("id") on delete cascade, foreign key("loyalty_reward_id") references "loyalty_rewards"("id") on delete cascade, foreign key("approved_by") references "users"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "cocktail_dish" ("id" integer primary key autoincrement not null, "cocktail_id" integer not null, "dish_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("cocktail_id") references "cocktails"("id") on delete cascade, foreign key("dish_id") references "dishes"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "cover_carousel_items" ("id" integer primary key autoincrement not null, "title" varchar not null, "subtitle" text, "image_path" varchar not null, "link_label" varchar, "link_url" text, "position" integer not null default '0', "visible" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "dish_recommendations" ("id" integer primary key autoincrement not null, "dish_id" integer not null, "recommended_dish_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("dish_id") references "dishes"("id") on delete cascade, foreign key("recommended_dish_id") references "dishes"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "extras" ("id" integer primary key autoincrement not null, "name" varchar not null, "price" numeric not null default '0', "description" varchar, "view_scope" varchar not null default 'global', "active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "extra_assignments" ("id" integer primary key autoincrement not null, "extra_id" integer not null, "assignable_type" varchar not null, "assignable_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("extra_id") references "extras"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "table_sessions" ("id" integer primary key autoincrement not null, "server_id" integer not null, "table_label" varchar not null, "party_size" integer not null, "guest_name" varchar not null, "guest_email" varchar not null, "guest_phone" varchar not null, "qr_token" varchar not null, "status" varchar check ("status" in ('active', 'closed', 'expired')) not null default 'active', "expires_at" datetime, "closed_at" datetime, "created_at" datetime, "updated_at" datetime, foreign key("server_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "orders" ("id" integer primary key autoincrement not null, "table_session_id" integer not null, "server_id" integer not null, "status" varchar check ("status" in ('pending', 'confirmed', 'cancelled')) not null default 'pending', "confirmed_at" datetime, "cancelled_at" datetime, "created_at" datetime, "updated_at" datetime, foreign key("table_session_id") references "table_sessions"("id") on delete cascade, foreign key("server_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "order_items" ("id" integer primary key autoincrement not null, "order_id" integer not null, "itemable_type" varchar not null, "itemable_id" integer not null, "name" varchar not null, "quantity" integer not null default '1', "unit_price" numeric not null default '0', "notes" text, "category_scope" varchar, "category_id" integer, "category_name" varchar, "category_order" integer not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("order_id") references "orders"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "order_item_extras" ("id" integer primary key autoincrement not null, "order_item_id" integer not null, "extra_id" integer, "name" varchar not null, "price" numeric not null default '0', "quantity" integer not null default '1', "created_at" datetime, "updated_at" datetime, foreign key("order_item_id") references "order_items"("id") on delete cascade, foreign key("extra_id") references "extras"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "cocktail_categories" ("id" integer primary key autoincrement not null, "name" varchar not null, "order" integer not null default '0', "show_on_cover" tinyint(1) not null default '0', "cover_title" varchar, "cover_subtitle" varchar, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "wine_categories" ("id" integer primary key autoincrement not null, "name" varchar not null, "order" integer not null default '0', "show_on_cover" tinyint(1) not null default '0', "cover_title" varchar, "cover_subtitle" varchar, "created_at" datetime, "updated_at" datetime);
